mapper.insert:
INSERT INTO
detail_schedule (id_schedule, shift, task_final, task_initial, time_final, time_initial) 
VALUES (#{id_schedule}, #{shift}, #{task_final}, #{task_initial}, #{time_final}, #{time_initial});

mapper.select:
SELECT id, id_schedule, shift, task_final, task_initial, time_final, time_initial
FROM detail_schedule
WHERE id = #{id};

SELECT id, id_schedule, shift, task_final, task_initial, time_final, time_initial
FROM detail_schedule
WHERE id_schedule = #{id_schedule};
ORDER BY id desc;
