mapper.select:
SELECT id, business_job_cost, final_job_cost, date_from, date_to, contractual_level, bill_center, id_user
FROM job_cost
WHERE bill_center=#{bill_center};

SELECT id, business_job_cost, final_job_cost, date_from, date_to, contractual_level, bill_center, id_user
FROM job_cost
WHERE id_user=#{id_user};

select id,description 
from billcenter  
where id = #{idBillCenter};

