mapper.select:
SELECT id, date_schedule, user, editor, controller,
from_time, to_time, note
FROM schedule
WHERE date_schedule =
#{date_schedule};
ORDER BY date_schedule desc;

SELECT id, id_schedule, shift, task_final, task_initial,
time_final, time_initial
FROM detail_schedule
WHERE id_schedule =
#{id_schedule}
ORDER BY shift asc;
