mapper.insert:
INSERT INTO
schedule (date_schedule, user, editor, controller, from_time, to_time, note) 
VALUES (#{date_schedule}, #{user}, #{editor}, #{controller}, #{from_time}, #{to_time}, #{note});

mapper.select:
SELECT id, date_schedule, user, editor, controller, from_time, to_time, note
FROM schedule 
WHERE id = #{id};

SELECT id, date_schedule, user, editor, controller, from_time, to_time, note
FROM schedule 
WHERE date_schedule = #{date_schedule};
ORDER BY date_schedule desc;
