mapper.select:
SELECT s.id, s.idship, s.volume, s.note, s.arrivaldate, s.departuredate, sh.name FROM scheduleship s, ship sh
WHERE (sh.name like CONCAT('%',#{my_full_text_search}, '%') and s.idship = sh.id)
order by s.arrivaldate DESC;

SELECT d.shift, d.id, d.idscheduleship, s.name, d.operation, ss.note,ss.arrivaldate, ss.departuredate, d.iduser, d.handswork, d.menwork, d.shiftdate, u.firstname, u.lastname FROM detailscheduleship d, user u, ship s, scheduleship ss where
(s.name like CONCAT('%',#{full_text_search}, '%') ) and
d.idscheduleship = ss.id and ss.idship = s.id and d.iduser = u.id and d.shiftdate=#{shiftdate} order by d.shift asc;
