mapper.select:
SELECT ss.id AS rif_sws, ss.rif_mct, (SELECT cc.name FROM customer cc WHERE cc.id = ss.customer_id) AS customer, s.name AS shipname, s.id AS id_ship, dss.shift, d.timework AS time_work, d.franchise_timework, (SELECT c.number FROM crane c WHERE d.id_crane = c.id) AS crane, d.crane_gtw, dss.shiftdate AS date_request, d.volume, d.franchise_volume, d.volumeunderboard, d.franchise_volumeunderboard, d.volumeunderboard_sws, d.franchise_volumeunderboard_sws, d.volume_tw_mct, d.franchise_volume_tw_mct, d.notedetail, d.invoicing_cycle, dss.sky, dss.rain, dss.wind, dss.windyday, dss.temperature, dss.first_down, dss.last_down, dss.person_down, dss.person_onboard, dss.worked, d.menwork_activityh FROM ship s, scheduleship ss, detailscheduleship dss LEFT JOIN detailfinalscheduleship d ON (dss.id = d.iddetailscheduleship) WHERE dss.idscheduleship = ss.id AND s.id = ss.idship
AND dss.shiftdate >= #{dt_arg_from} AND dss.shiftdate <= #{dt_arg_to}
and dss.shiftdate = #{dt_arg_to}
and dss.shiftdate = #{dt_arg_from}
and s.name like CONCAT('%', #{searchText},'%')
and s.shiptype like CONCAT('%',#{shipType},'%')
and s.line like CONCAT('%',#{shipLine},'%')
and s.shipcondition like CONCAT('%',#{shipCondition},'%')
and ss.id = #{rifSWS}
and (ss.rif_mct is null || length(ss.rif_mct) = 0)
and ss.rif_mct = #{rifMCT}
and dss.shift = #{shift}
and d.invoicing_cycle = #{invoicing_cycle_search}
and ss.id_service is null
and ss.id_service = #{idService}
order by date_request, s.name , dss.shift

select s.name as shipname, ss.idship as id_ship, ss.id as rif_sws, ss.rif_mct as rif_mct, sum(d.timework - d.franchise_timework) as time_work, sum(d.volume - d.franchise_volume) as volume, sum(d.volumeunderboard - d.franchise_volumeunderboard) as volumeunderboard, sum(d.volumeunderboard_sws - d.franchise_volumeunderboard_sws) as volumeunderboard_sws, sum(d.volume_tw_mct - d.franchise_volume_tw_mct) as volume_tw_mct from ship s, scheduleship ss, detailscheduleship dss, detailfinalscheduleship d WHERE ss.idship is not null and ss.idship is not null and dss.idscheduleship = ss.id and s.id = ss.idship and d.iddetailscheduleship = dss.id
and dss.shiftdate >= #{dt_arg_from} and dss.shiftdate <= #{dt_arg_to}
and dss.shiftdate = #{dt_arg_to}
and dss.shiftdate = #{dt_arg_from}
and s.name like CONCAT('%', #{searchText},'%')
and s.shiptype like CONCAT('%',#{shipType},'%')
and s.line like CONCAT('%',#{shipLine},'%')
and s.shipcondition like CONCAT('%',#{shipCondition},'%')
and ss.id = #{rifSWS}
and (ss.rif_mct is null || length(ss.rif_mct) = 0)
and ss.rif_mct = #{rifMCT}
and d.invoicing_cycle = #{invoicing_cycle}
and ss.id_service is null
and ss.id_service = #{serviceId}
group by rif_sws order by s.name

SELECT SUM(IFNULL(dis.handswork, 0)) AS handswork, SUM(IFNULL(dis.handswork_program, 0)) AS handswork_program, SUM(IFNULL(dis.menwork, 0)) AS menwork, MONTH(dis.shiftdate) AS monthInvoice, SUM(IFNULL(dis.windyday, 0)) AS windyday FROM detailscheduleship dis, scheduleship ss WHERE YEAR(dis.shiftdate) = #{year} AND dis.idscheduleship = ss.id AND ss.id_service = #{idService} AND EXISTS(SELECT * FROM detailfinalscheduleship dfs WHERE dfs.invoicing_cycle IS NOT NULL AND iddetailscheduleship = dis.id)
and shift=#{shift}
GROUP BY monthInvoice

SELECT SUM(IFNULL(dis.handswork, 0)) AS handswork, SUM(IFNULL(dis.handswork_program, 0)) AS handswork_program, SUM(IFNULL(dis.menwork, 0)) AS menwork, MONTH(dis.shiftdate) AS monthInvoice, SUM(IFNULL(dis.windyday, 0)) AS windyday FROM detailscheduleship dis, scheduleship ss WHERE YEAR(dis.shiftdate) = #{year} AND dis.idscheduleship = ss.id AND ss.id_service = #{idService}
and shift=#{shift}
GROUP BY monthInvoice;

SELECT COUNT(DISTINCT ss.id) AS shipnumber, dfs.invoicing_cycle AS monthInvoice FROM detailscheduleship dis LEFT JOIN detailfinalscheduleship dfs ON iddetailscheduleship = dis.id, scheduleship ss WHERE YEAR(dis.shiftdate) = #{year} AND dis.idscheduleship = ss.id AND ss.id_service = (SELECT sv.id AS id FROM service sv WHERE sv.isRZ = 1) AND dfs.invoicing_cycle IS NOT NULL
AND dis.operation like CONCAT('%', #{operation},'%')
GROUP BY monthInvoice;

SELECT COUNT(DISTINCT ss.id) AS shipnumber, MONTH(dis.shiftdate) AS monthInvoice FROM detailscheduleship dis, scheduleship ss WHERE YEAR(dis.shiftdate) = #{year} AND dis.idscheduleship = ss.id AND ss.id_service = (SELECT sv.id AS id FROM service sv WHERE sv.isRZ = 1)
AND dis.operation like CONCAT('%', #{operation},'%')
GROUP BY monthInvoice;

SELECT *
FROM complaint
WHERE id=#{id};

SELECT *
FROM complaint
WHERE year_comp=#{year};

select sum(numberofcomplaint) as numberofcomplaint,
month_comp as month_date
from complaint
where year_comp=#{year} and
id_customer=#{id_customer}
group by month_comp;
