mapper.select:
select ss.id as rif_sws, ss.rif_mct, s.name as shipname, s.id as id_ship, dss.shift, d.timework as time_work, d.crane, d.crane_gtw, dss.shiftdate as date_request, d.volume, d.volumeunderboard, d.volumeunderboard_sws, d.volume_tw_mct, d.notedetail, d.invoicing_cycle, dss.sky, dss.rain, dss.wind, dss.temperature, dss.first_down, dss.last_down from ship s, scheduleship ss, detailscheduleship dss left join detailfinalscheduleship d ON (dss.id = d.iddetailscheduleship) where dss.idscheduleship = ss.id and s.id = ss.idship
and dss.shiftdate >= #{dt_arg_from} and dss.shiftdate <= #{dt_arg_to}
and dss.shiftdate = #{dt_arg_from}
and s.name like CONCAT('%', #{searchText},'%')
and ss.id = #{rifSWS}
and ss.rif_mct = #{rifMCT}
and dss.shift = #{shift}
and d.invoicing_cycle = #{invoicing_cycle_search}
and MONTH(dss.shiftdate) = #{working_cycle_search}
order by s.name , date_request, dss.shift

select s.name as shipname, ss.idship as id_ship, ss.id as rif_sws, ss.rif_mct as rif_mct, sum(d.timework) as time_work, sum(d.volume) as volume, sum(d.volumeunderboard) as volumeunderboard, sum(d.volumeunderboard_sws) as volumeunderboard_sws, sum(d.volume_tw_mct) as volume_tw_mct from ship s, scheduleship ss, detailscheduleship dss, detailfinalscheduleship d WHERE ss.idship is not null and ss.idship is not null and dss.idscheduleship = ss.id and s.id = ss.idship and d.iddetailscheduleship = dss.id
and dss.shiftdate >= #{dt_arg_from} and dss.shiftdate <= #{dt_arg_to}
and dss.shiftdate = #{dt_arg_from}
and s.name like CONCAT('%', #{searchText},'%')
group by rif_sws order by s.name
