mapper.select:
SELECT t.id, t.title, t.certificate_date, t.expiration_date, t.description, t.user_id, t.trainer_type, t.trainer, t.training_task, t.training_level, t.note FROM training_certificate t, user u WHERE t.user_id = u.id
AND t.id = #{id}
AND t.user_id = #{user_id}
AND (t.title LIKE CONCAT('%', #{full_text_search}, '%') OR t.description LIKE CONCAT('%', #{full_text_search}, '%') OR t.trainer_type LIKE CONCAT('%', #{full_text_search}, '%') OR t.trainer LIKE CONCAT('%', #{full_text_search}, '%') OR t.training_task LIKE CONCAT('%', #{full_text_search}, '%') OR t.training_level LIKE CONCAT('%', #{full_text_search}, '%') OR t.note LIKE CONCAT('%', #{full_text_search}, '%'))
AND (CURDATE() <= t.expiration_date OR t.expiration_date IS NULL)
AND (CURDATE() >= t.expiration_date AND t.expiration_date IS NOT NULL)
