mapper.select:
SELECT t.id,t.title,t.certificate_date,t.expiration_date,t.description,t.user_id,t.trainer_type, t.trainer, t.training_task, t.training_level, t.note FROM training_certificate t, user u WHERE
t.user_id = u.id
and t.id = #{id}
and t.user_id = #{user_id}
and (t.title like CONCAT('%',#{full_text_search},'%') or t.description like CONCAT('%',#{full_text_search},'%') or t.trainer_type like CONCAT('%',#{full_text_search},'%') or t.trainer like CONCAT('%',#{full_text_search},'%') or t.training_task like CONCAT('%',#{full_text_search},'%') or t.training_level like CONCAT('%',#{full_text_search},'%') or t.note like CONCAT('%',#{full_text_search},'%'))
and CURDATE() <= t.expiration_date
and CURDATE() >= t.expiration_date
;
